/**
 * Service Worker for cache busting and updates
 * This file is automatically generated by the build-sw.js script
 * DO NOT edit this file directly - edit the template and run the build
 */

const CACHE_NAME = 'bookamot-cache-0.1.0-4402a8c0'
const BUILD_VERSION = '0.1.0-4402a8c0'
const BUILD_TIMESTAMP = '1770205562454'

// Resources that should always be fetched from network
const NETWORK_FIRST = [
  '/api/',
  '/auth/',
]

// Resources that can be served from cache
const CACHE_FIRST = [
  '/_next/static/',
  '/_next/image',
]

/**
 * Service Worker installation
 */
self.addEventListener('install', (event) => {
  console.log('[SW] Installing Service Worker, version:', BUILD_VERSION)
  
  event.waitUntil(
    caches.open(CACHE_NAME).then((cache) => {
      console.log('[SW] Cache opened')
      return cache.addAll([
        '/',
        '/offline',
      ])
    })
  )
  
  // Force immediate activation of new Service Worker
  self.skipWaiting()
})

/**
 * Service Worker activation
 */
self.addEventListener('activate', (event) => {
  console.log('[SW] Activating Service Worker, version:', BUILD_VERSION)
  
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames
          .filter((name) => name !== CACHE_NAME)
          .map((name) => {
            console.log('[SW] Deleting old cache:', name)
            return caches.delete(name)
          })
      )
    })
  )
  
  // Take immediate control of all pages
  return self.clients.claim()
})

/**
 * Intercept requests
 */
self.addEventListener('fetch', (event) => {
  const { request } = event
  const url = new URL(request.url)
  
  // Ignore non-GET requests
  if (request.method !== 'GET') {
    return
  }
  
  // Network First strategy for APIs
  if (NETWORK_FIRST.some((path) => url.pathname.startsWith(path))) {
    event.respondWith(networkFirstStrategy(request))
    return
  }
  
  // Cache First strategy for static resources
  if (CACHE_FIRST.some((path) => url.pathname.startsWith(path))) {
    event.respondWith(cacheFirstStrategy(request))
    return
  }
  
  // Network First strategy for pages
  event.respondWith(networkFirstStrategy(request))
})

/**
 * Network First strategy
 * Try to fetch from network first, use cache if it fails
 */
async function networkFirstStrategy(request) {
  try {
    const response = await fetch(request)
    
    // Cache only valid responses
    if (response && response.status === 200) {
      const cache = await caches.open(CACHE_NAME)
      cache.put(request, response.clone())
    }
    
    return response
  } catch (error) {
    console.log('[SW] Network failed, trying cache:', request.url)
    
    const cachedResponse = await caches.match(request)
    if (cachedResponse) {
      return cachedResponse
    }
    
    // If it's a navigation and there's no cache, return offline page
    if (request.mode === 'navigate') {
      const offlinePage = await caches.match('/offline')
      if (offlinePage) {
        return offlinePage
      }
    }
    
    throw error
  }
}

/**
 * Cache First strategy
 * Try to fetch from cache first, use network if not found
 */
async function cacheFirstStrategy(request) {
  const cachedResponse = await caches.match(request)
  
  if (cachedResponse) {
      // Check if cache is not too old (24 hours)
    const cacheDate = cachedResponse.headers.get('date')
    if (cacheDate) {
      const cacheAge = Date.now() - new Date(cacheDate).getTime()
      const maxAge = 24 * 60 * 60 * 1000 // 24 hours
      
      if (cacheAge < maxAge) {
        return cachedResponse
      }
    } else {
      return cachedResponse
    }
  }
  
  try {
    const response = await fetch(request)
    
    if (response && response.status === 200) {
      const cache = await caches.open(CACHE_NAME)
      cache.put(request, response.clone())
    }
    
    return response
  } catch (error) {
    if (cachedResponse) {
      return cachedResponse
    }
    throw error
  }
}

/**
 * Service Worker messages
 */
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting()
  }
  
  if (event.data && event.data.type === 'GET_VERSION' && event.ports && event.ports[0]) {
    event.ports[0].postMessage({
      version: BUILD_VERSION,
      timestamp: BUILD_TIMESTAMP,
    })
  }
})
